type Track = {
    title: string;
    releaseDate: string;
};

type Artist = {
    name: string;
    releaseDate: string;
};

type SearchResult = Track | Artist;


function getNameOrTitle(result: SearchResult): string {
    
    if ('title' in result) {

        return result.title;
    } else {
        return result.name;
    }
}

let trackResult: SearchResult = { title: "Bohemian Rhapsody", releaseDate: "1975" };
let artistResult: SearchResult = { name: "Queen", releaseDate: "1970" };


console.log(`Track result name/title: ${getNameOrTitle(trackResult)}`);
console.log(`Artist result name/title: ${getNameOrTitle(artistResult)}`);